import { reactive, toRefs, computed } from "vue";
import CatalogPagination from '@/components/Catalog/CatalogPagination.vue';
export default {
  components: {
    CatalogPagination
  },
  props: {
    productsList: {
      type: Object,
      default() {
        return {};
      }
    }
  },
  data() {
    return {
      isProductLoading: Boolean
    };
  },
  setup(props) {
    const state = reactive({
      currentPage: 1,
      rowsPerPage: 6,
      productsList: toRefs(props),
      pageCount: computed(() => Math.ceil(props.productsList.length / state.rowsPerPage)),
      paginatedData: computed(() => props.productsList.slice((state.currentPage - 1) * state.rowsPerPage, state.currentPage * state.rowsPerPage))
    });
    console.log(props.productsList);
    function setCurrentPage(number) {
      state.currentPage = number;
    }
    return {
      ...toRefs(state),
      setCurrentPage
    };
  }
};
