import { reactive, toRefs, computed } from "vue";
import CatalogPagination from '@/components/Catalog/CatalogPagination.vue';
export default {
  components: {
    CatalogPagination
  },
  props: {
    productsList: {
      type: Object,
      default() {
        return {};
      }
    }
  },
  data() {
    return {
      isProductLoading: Boolean
    };
  },
  setup() {
    const state = reactive({
      currentPage: 1,
      data: new Array(500).fill(1).map((_, i) => i),
      rowsPerPage: 100,
      pageCount: computed(() => Math.ceil(state.data.length / state.rowsPerPage)),
      paginatedData: computed(() => state.data.slice((state.currentPage - 1) * state.rowsPerPage, state.currentPage * state.rowsPerPage))
    });
    function setCurrentPage(number) {
      state.currentPage = number;
    }
    return {
      ...toRefs(state),
      setCurrentPage
    };
  }
};
