import { createStore } from 'vuex'

const store = createStore({
    state () {
      return {
        products: []
      }
    },
    actions: {
      GET_PRODUCTS_F

    },
    getters: {
      PRODUCTS(state) {
        return state.products
      }
    }
})

export default store
