import { reactive, toRefs, computed } from "vue";
import CatalogPagination from '@/components/Catalog/CatalogPagination.vue';
export default {
  components: {
    CatalogPagination
  },
  props: {},
  data() {
    return {
      isProductLoading: Boolean
    };
  },
  setup() {
    const state = reactive({
      currentPage: 1,
      rowsPerPage: 6,
      pageCount: computed(() => Math.ceil(state.data.length / state.rowsPerPage)),
      paginatedData: computed(() => this.productsList.slice((state.currentPage - 1) * state.rowsPerPage, state.currentPage * state.rowsPerPage))
    });
    function setCurrentPage(number) {
      state.currentPage = number;
    }
    return {
      ...toRefs(state),
      setCurrentPage
    };
  }
};
