import Pagination from 'vuejs-paginate-next';
import { toRefs, ref, onBeforeMount } from "vue";
export default {
  props: {
    currentPage: Number,
    pageCount: Number
  },
  components: {
    paginate: Pagination
  },
  setup(props, context) {
    const {
      currentPage: onCurrentPage,
      pageCount: onPageCount
    } = toRefs(props);
    const currentPageNumber = ref(onCurrentPage.value);
    const clickCallback = () => {
      console.log("Call Back Triggered", `Current Page - ${currentPageNumber.value}`);
      context.emit("set-currentpage", currentPageNumber.value);
    };
    onBeforeMount(() => {
      console.log(onCurrentPage.value, onPageCount.value);
    });
    return {
      clickCallback,
      onCurrentPage,
      onPageCount,
      currentPageNumber
    };
  }
};
